import React from 'react';

const GoodsCard = ({ image, name, price }) => {
    return (
        <div className="goods-gallery">
            {products.map(product => (
                <div className="goods-card">
                    <img src={image} alt={name}/>
                    <h3>{name}</h3>
                    <p>{price} грн</p>
                </div>
        ))}
    </div>
)
    ;
};

export default GoodsCard;
