// src/components/GoodsCard.js
import React from 'react';

const GoodsCard = () => {
    // Масив з даними про товари
    const goods = [
        { image: "/apple.webp", name: "Apple", price: "100" },
        { image: "/carrot.webp", name: "Carrot", price: "150" },
        { image: "/kiwi.webp", name: "Kiwi", price: "200" },
        { image: "/pineapple.jpg", name: "Pineapple", price: "250" },
        { image: "/watermelon.jpg", name: "Watermelon", price: "300" },
        { image: "/product6.jpg", name: "Товар 6", price: "350" },
    ];

    return (
        <div className="goods-gallery">
            {goods.map((item, index) => (
                <div key={index} className="goods-card">
                    <img src={item.image} alt={item.name} />
                    <h3>{item.name}</h3>
                    <p>{item.price} грн</p>
                </div>
            ))}
        </div>
    );
};

export default GoodsCard;
